import { useState, useEffect } from 'react';

const Fade = (props) => {
    const [fade, setFade] = useState({
        fade: 'fade-in',
    });

    useEffect(() => {
        if (props.fade === 1) {
            setFade({
                fade: 'fade-in',
            })
        } else if (props.fade === 2) {
            setFade({
                fade: 'fade-out',
            })
        }
    }, [props.fade])

    return (
        <>
            <h1
                className={fade.fade}
            >{props.text}</h1>
        </>
    )
}

export default Fade
