import Matter, { Body } from 'matter-js';

const Letters = (param) => {
    var Bodies = Matter.Bodies;

    const bTopPos = { x: 500, y: 50 };
    const letterWidth = param.w / 19;
    const spaceBetweenLetters = letterWidth + param.w / 384;
    const letterHeight = param.h / 25;


    // B
    var bOuterTop = Bodies.rectangle(bTopPos.x, bTopPos.y, letterWidth, letterHeight / 2, {
        chamfer: { radius: [0, 40, 40, 0] },
        render: {
            opacity: 0.5,
        }
    });
    var bOuterBot = Bodies.rectangle(500, 130, letterWidth, leterHei, {
        chamfer: { radius: [0, 40, 40, 0] },
        render: {
            opacity: 0.5,
        }
    });
    var bInnerTop = Bodies.rectangle(500, 50, 50, 40, {
        chamfer: { radius: [0, 20, 20, 0] },
        render: {
            opacity: 0.5,
        }
    });
    var bInnerBot = Bodies.rectangle(500, 130, 50, 40, {
        chamfer: { radius: [0, 20, 20, 0] },
        render: {
            opacity: 0.5,
        }
    });
    var bLeftLine = Bodies.rectangle(473, 90, 40, 163, {
        render: {
            opacity: 0.5,
        }
    });
    var bLetter = Matter.Body.create({
        parts: [bInnerTop, bInnerBot, bOuterTop, bOuterBot, bLeftLine],
    });

// ----------------------------------------------------------------------------------------

    // R
    const rPos = bTopPos.x + spaceBetweenLetters;

    var rOuterTop = Bodies.rectangle(rPos, 50, 100, 85, {
        chamfer: { radius: [0, 40, 40, 0] },
        render: {
            opacity: 0.5,
        }
    });
    var rInnerTop = Bodies.rectangle(rPos, 50, 50, 40, {
        chamfer: { radius: [0, 20, 20, 0] },
        render: {
            opacity: 0.5,
        }
    });
    var rLeftLine = Bodies.rectangle(rPos - param.w / 70, 90, 40, 163, {
        render: {
            opacity: 0.5,
        }
    });
    var rLetter = Matter.Body.create({
        isStatic: true,
        parts: [rOuterTop, rInnerTop, rLeftLine],
    });

// ----------------------------------------------------------------------------------------


    return ({
        bLetter: bLetter,
        rLetter: rLetter,
    })
}

export default Letters;
