import Matter, { Body, Runner } from 'matter-js'
import React, { useEffect, useState } from 'react';

const MatterEngine = () => {
    const testObj = React.createRef();
    let boxA;

    useEffect(() => {
        var Engine = Matter.Engine;
        var Render = Matter.Render;
        var World = Matter.Runner;
        var Bodies = Matter.Bodies;
        var Composite = Matter.Composite;

        var engine = Engine.create();

        var render = Render.create({
            element: testObj.current,
            engine: engine,
            options: {
                width: 2000,
                height: 1000,
                wireframes: false,
            }
        });

        boxA = Bodies.rectangle(400, 200, 120, 80);




        // Letter B
        var bInner;
        var bOuter = Bodies.rectangle(500, 50, 100, 80, {
            chamfer: { radius: [0, 40, 40, 0] },
            render: {
                opacity: 0.5,
            }
        });

        var bLeftLine = Bodies.rectangle(500, 50, 20, 300, {
            chamfer: { radius: [0, 40, 40, 0] },
            render: {
                opacity: 0.5,
            }
        });

        // var bLetter = Body.create({
        //     parts: [bInner, bOuter],
        // });


        var bLetter = Matter.Body.create();

        bLetter.setParts([bLeftLine, bOuter]); 

        //


        var ground = Bodies.rectangle(400, 610, 800, 200,{
            isStatic: true,
        });

        Composite.add(engine.world, [bLetter, ground]);

        Render.run(render);

        var runner = Runner.create();

        // run the engine
        Runner.run(runner, engine);

    }, []);

    const handleDown = (e) => {
        if (e.key === 'ArrowRight') {
            Body.applyForce(boxA, {
                x: boxA.position.x,
                y: boxA.position.y,
            }, {
                x: 0.1,
                y: 0,
            });
        } else if (e.key === 'ArrowLeft') {
            Body.applyForce(boxA, {
                x: boxA.position.x,
                y: boxA.position.y,
            }, {
                x: -0.1,
                y: 0,
            });
        }
    }

    return (
        <div
            onKeyDown={handleDown}
            tabIndex={-1}
            ref={testObj}
            style={{ width: '100%', height: '100%' }}
        >
        </div>
    )
}

export default MatterEngine;
