import { useState, useEffect } from 'react';

const Fade = ({ text, onChange }) => {
    const [fade, setFade] = useState({
        fade: 'fade-out',
    });

    useEffect(() => {
        onChange([])

        if (props.fade === 1) {
            setFade({
                fade: 'fade-in',
            })
        } else if (props.fade === 2) {
            setFade({
                fade: 'fade-out',
            })
        }
    }, [props.fade])

    return (
        <div>
            <h1
                className={fade.fade}
            >{text}</h1>
        </div>
    )
}

export default Fade;
