import { useState, useEffect } from 'react';
import pp from './Assets/linkedin_profile.png';

const Fade = ({ text, onChange }) => {
    const [fade, setFade] = useState({
        fade: 'fade-out',
    });

    useEffect(() => {
        onChange(setFade);
    }, [])

    return (
        <div
        className={fade.fade}
        class='profile'
        >
            <img
                src={pp}
            ></img>
            <h1>
            {text}</h1>
        </div>
    )
}

export default Fade;
