import Matter, { Body, Runner } from 'matter-js'
import React, { useEffect, useState } from 'react';

const MatterEngine = () => {
    const testObj = React.createRef();

    const [upPressed, setUpPressed] = useState(false);

    useEffect(() => {
        var Engine = Matter.Engine;
        var Render = Matter.Render;
        var World = Matter.Runner;
        var Bodies = Matter.Bodies;
        var Composite = Matter.Composite;

        var engine = Engine.create();

        var render = Render.create({
            element: testObj.current,
            engine: engine,
            options: {
                width: 2000,
                height: 1000,
                wireframes: false,
            }
        });

        // create two boxes and a ground
        var boxA = Bodies.rectangle(400, 200, 80, 80);
        var boxB = Bodies.rectangle(450, 50, 80, 80);
        var ground = Bodies.rectangle(400, 610, 800, 200, { isStatic: true });

        // add all of the bodies to the world
        Composite.add(engine.world, [boxA, boxB, ground]);

        // run the renderer
        Render.run(render);

        // create runner
        var runner = Runner.create();

        // run the engine
        Runner.run(runner, engine);

        if (upPressed) {
            console.log("up");
            Body.applyForce(boxA, {
                x: boxA.position.x,
                y: boxA.position.y,
            }, {
                x: 2,
                y: 2,
        });
    }   

    }, []);

    const handleDown = (e) => {
        if (e.key === 'ArrowUp') {
            setUpPressed(true);
        }
    }

    return (
        <div
            onKeyDown={handleDown}
            tabIndex={-1}
            ref={testObj}
            style={{ width: '100%', height: '100%' }}
        >
        </div>
    )
}

export default MatterEngine;
