import Matter, { Body, Runner } from 'matter-js'
import React, { useEffect, useState } from 'react';
import Letters from './Letters';

const MatterWorld = (props) => {
    const matter = React.createRef();
    let boxA;
    let trap;
    let oLetter;
    let bLetter;

    useEffect(() => {
        matter.current.focus();

        console.log(props.width);
        console.log(props.height);

        const screen = {
            w: props.width,
            h: props.height,
        }

        var Engine = Matter.Engine;
        var Render = Matter.Render;
        var World = Matter.Runner;
        var Bodies = Matter.Bodies;
        var Composite = Matter.Composite;

        var engine = Engine.create();

        var render = Render.create({
            element: matter.current,
            engine: engine,
            options: {
                width: 2000,
                height: 1000,
                wireframes: false,
                background: '#fffff',
            }
        });

        boxA = Bodies.rectangle(400, 200, 80, 80, {
            render: {
                fillStyle: 'black',
            }
        });

        trap = Bodies.fromVertices(600, 20, [
            {x: 0, y: 0},
            {x: 0, y: 20},
            {x: 20, y: 20},
            {x: 20, y: 0},
        ]);

        // Letter B
        var bLetter = Letters(screen).bLetter;
        Body.scale(bLetter, 1, 1);

        // Letter R
        var rLetter = Letters(screen).rLetter;

        var aLetter = Letters(screen).aLetter;

        var nLetter = Letters(screen).nLetter;
        Body.scale(nLetter, 0.9, 1);

        var dLetter = Letters(screen).dLetter;

        var ground = Bodies.rectangle(0.5 * screen.w, 0.66 * screen.h, screen.w, 200, {
            isStatic: true,
        });

        var leftBound = Bodies.rectangle(-20, screen.h / 2, 20, screen.h, {
            isStatic: true,
        });

        var rightBound = Bodies.rectangle(screen.w + 20, screen.h / 2, 20, screen.h, {
            isStatic: true,
        });

        var oLetter = Bodies.circle(1100, 90, 85, {
            render: { opacity: 0.5 },
        });

        Composite.add(engine.world,
            [leftBound, rightBound, boxA, bLetter, rLetter, aLetter, nLetter, dLetter, oLetter, ground]
        );

        Render.run(render);

        var runner = Runner.create();

        // run the engine
        Runner.run(runner, engine);

    }, []);

    const handleDown = (e) => {
        if (e.key === 'ArrowRight') {
            Body.applyForce(boxA, {
                x: boxA.position.x,
                y: boxA.position.y,
            }, {
                x: 0.1,
                y: 0,
            });
        } else if (e.key === 'ArrowLeft') {
            Body.applyForce(boxA, {
                x: boxA.position.x,
                y: boxA.position.y,
            }, {
                x: -0.1,
                y: 0,
            });
        } else if (e.key === 'ArrowUp') {
            Body.applyForce(boxA, {
                x: boxA.position.x,
                y: boxA.position.y,
            }, {
                x: 0,
                y: -0.3,
            });
        }
    }

    return (
        <div
            ref={matter}
            onKeyDown={handleDown}
            tabIndex={-1}
        >
        </div>
    )
}

export default MatterWorld;
